def creatureGen(*include_tags, exclude_tags=[]):
    monsters_with_tags = []
    
    for monster, monster_tags in monster_list.items():
        if all(tag in monster_tags for tag in include_tags) and not any(tag in monster_tags for tag in exclude_tags):
            monsters_with_tags.append(monster)
    if not monsters_with_tags:
        return None
    return monsters_with_tags


def petGen(*tags):
    in_tags = ['pet', 'beast', 'mm'] + list(tags)
    ex_tags = ['aquatic']

    monster = (random.choice(creatureGen(*in_tags, exclude_tags=ex_tags)))
    if monster is None:
        return("No monsters found with the specified tags.")
    else:
        return monster


