def encounterGen():
    in_tags_input = input("Enter tags to include (separated by commas): ")
    user_in_tags = [tag.strip() for tag in in_tags_input.split(',')]
    ex_tags_input = input("Enter tags to exclude (separated by commas): ")
    user_ex_tags = [tag.strip() for tag in ex_tags_input.split(',')]
    in_tags = ['mm'] + list(user_in_tags)
    ex_tags = [] + list(user_ex_tags)

    encounter = (random.choice(creatureGen(*in_tags, exclude_tags=ex_tags)))
    if encounter is None:
        return("No monsters found with the specified tags.")
    else:
        return encounter



